import React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

export default class ContactForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: this.props.firstName,
      lastName: this.props.lastName,
      phoneNumber: this.props.phoneNumber,
      emailAddress: this.props.emailAddress,
      address: this.props.address,
      isReadOnly: this.props.isReadOnly,
    };
  }

  edit() {
    this.setState({isReadOnly: false});
  }

  save() {
    this.setState({isReadOnly: true});
    this.props.handleSavePress(
      this.state.firstName,
      this.state.lastName,
      this.state.phoneNumber,
      this.state.emailAddress,
      this.state.address,
    )
  }

  render() {
    let saveButton;
    if (this.state.isReadOnly === false) {
      saveButton =
        <Button
          variant='contained'
          onClick={() => this.save()}
        >
          Save
        </Button>
    }

    return (
      <Box
        className='App'
        component='form'
        sx={{
          '& .MuiTextField-root': { m: 1, width: '40ch' },
          width: '100%',
          bgcolor: 'background.paper',
        }}
        noValidate
        autoComplete="off"
      >
        <form>
          <div>
            <label>First Name
              <input readOnly className='form' type='text' name='first name' defaultValue={this.state.firstName} />
            </label>
          </div>
          <div>
            <label>Last Name
              <input readOnly className='form' type='text' name='last name' defaultValue={this.state.lastName} />
            </label>
          </div>
          <div>
            <label>Phone Number
              <input readOnly className='form' type='text' name='phone number' defaultValue={this.state.phoneNumber} />
            </label>
          </div>
          <div>
            <label>Email Address
              <input readOnly className='form' type='text' name='email' defaultValue={this.state.emailAddress} />
            </label>
          </div>
          <div>
            <label>Address
              <input readOnly className='form' type='text' name='address' defaultValue={this.state.address} />
            </label>
          </div>
        </form>
        <div>
          {saveButton}
        </div>
      </Box>
    )
  }
}
